mapper.update:
UPDATE lend
SET status='lent', enddate = sysdate + #{daysOfLend}, startdate = sysdate
WHERE num = #{num}

UPDATE lend
SET status='rejected'
WHERE num = #{num}

UPDATE lend
SET status='returned', returndate=sysdate
WHERE num = #{num}

UPDATE lend
SET status = 'delay_returned', returndate=sysdate
WHERE num=#{num}
